import React from 'react';
import axios from 'axios';

import './App.css';

class OrderLine extends React.Component {

  state = {
    OrderLineID: '',
    OrderID: '',
    ProductID: '',
    QTY: '',
    posts: []
  };

  componentDidMount = () => {
    this.getOrderLines();
  };

  getOrderLines = () => {
    axios.get('/api/order-lines')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved');
      })
      .catch(() => {
        // alert('error retrieving data');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({ [name]: value });
  };

  submit = (event) => {
    event.preventDefault();

    const payload = {
        OrderLineID: this.state.OrderLineID,
        OrderID: this.state.OrderID,
        ProductID: this.state.ProductID,
        QTY: this.state.QTY
    };

    axios({
      url: 'http://localhost:3000/api/saveOrderLines',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInputs();
        this.getOrderLines();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  resetUserInputs = () => {
    this.setState({
        OrderLineID: '',
        OrderID: '',
        ProductID: '',
        QTY: ''
    });
  };

  displayOrderLinePosts = (posts) => {

    if (!posts.length) return null;

    return posts.map((post, index) => (
      <div className="individual" key={index}>
        <h3>Order Line ID: {post.OrderLineID}</h3>
        <p>Order ID: {post.OrderID}</p>
        <p>Product ID: {post.ProductID}</p>
        <p>QTY: {post.QTY}</p>
      </div>
    ));
  };

  render() {

    console.log('State: ', this.state);

    return(
      <div className="main-container-inventory">
        <h1>Order Lines</h1>
        <hr></hr>
          <div className="content-container">
              <div className="add">
                  <h4>Add to Order Lines</h4>
                  <hr></hr>
                  <form onSubmit={this.submit}>
                    <div className="form-input">
                      <input
                        type="text"
                        name="OrderLineID"
                        value={this.state.OrderLineID}
                        placeholder="Order Line ID"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="OrderID"
                        value={this.state.OrderID}
                        placeholder="Order ID"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="ProductID"
                        value={this.state.ProductID}
                        placeholder="Product ID"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="QTY"
                        value={this.state.QTY}
                        placeholder="QTY"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <button>Submit</button>
                  </form>
              </div>
          </div>
          <br></br>
        <div className="bin-posts">
          {this.displayOrderLinePosts(this.state.posts)}
        </div>

      </div>
    );
  }
}

export default OrderLine;
