import React from 'react';
import axios from 'axios';

import './App.css';

class Products extends React.Component {

  state = {
    ProductID: '',
    SKU: '',
    ProductDescription: '',
    posts: []
  };

  componentDidMount = () => {
    this.getProducts();
  };

  getProducts = () => {
    axios.get('/api/products')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved');
      })
      .catch(() => {
        // alert('error retrieving data');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({ [name]: value });
  };

  submit = (event) => {
    event.preventDefault();

    const payload = {
        ProductID: this.state.ProductID,
        SKU: this.state.SKU,
        ProductDescription: this.state.ProductDescription
    };

    axios({
      url: 'http://localhost:3000/api/saveProduct',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInputs();
        this.getProducts();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  resetUserInputs = () => {
    this.setState({
        ProductID: '',
        SKU: '',
        ProductDescription: ''
    });
  };

  displayProductPosts = (posts) => {

    if (!posts.length) return null;

    return posts.map((post, index) => (
      <div className="individual" key={index}>
        <h3>Product ID: {post.ProductID}</h3>
        <p>SKU: {post.SKU}</p>
        <p>ProductDescription: {post.ProductDescription}</p>
      </div>
    ));
  };

  render() {

    console.log('State: ', this.state);

    return(
      <div className="main-container">
        <h1>Products</h1>
          <div className="content-container">
              <div className="add">
                  <h4>Add a Product</h4>
                  <form onSubmit={this.submit}>
                    <div className="form-input">
                      <input
                        type="text"
                        name="ProductID"
                        value={this.state.ProductID}
                        placeholder="Product ID"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="SKU"
                        value={this.state.SKU}
                        placeholder="SKU"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="ProductDescription"
                        value={this.state.ProductDescription}
                        placeholder="Product Description"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <button>Submit</button>
                  </form>
              </div>
          </div>
          <br></br>
        <div className="bin-posts">
          {this.displayProductPosts(this.state.posts)}
        </div>

      </div>
    );
  }
}

export default Products;
