import React from 'react';
import axios from 'axios';
import Banner from './banner';

import './App.css';

class Products extends React.Component {

  state = {
    ProductID: '',
    SKU: '',
    ProductDescription: '',
    _id: '',
    posts: []
  };

  componentDidMount = () => {
    this.getProducts();
  };

  getProducts = () => {
    axios.get('/api/products')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved');
      })
      .catch(() => {
        // alert('error retrieving data');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({ [name]: value });
  };

  submit = (event) => {
    event.preventDefault();

    const payload = {
        ProductID: this.state.ProductID,
        SKU: this.state.SKU,
        ProductDescription: this.state.ProductDescription
        // _id: this.state._id
    };

    axios({
      url: 'http://localhost:3000/api/saveProduct',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInputs();
        this.getProducts();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  delete = (data, event) => {
    event.preventDefault();

    console.log('Data before sending to server: ', data._id);

    axios({
      url: 'http://localhost:3000/api/deleteProduct',
      method: 'POST',
      data: data._id
    })
      .then(() => {
        console.log('Data has been sent to the server');
        // this.resetUserInputs();
        // this.getProducts();
      })
      .catch(() => {
        console.log('Internal server error');
      });
      this.getProducts();
  };

  resetUserInputs = () => {
    this.setState({
        ProductID: '',
        SKU: '',
        ProductDescription: '',
        _id: ''
    });
  };

  displayProductPosts = (posts) => {

    if (!posts.length) return null;

    return posts.map((post, index) => (


      <ul>
        <li>
          <div className="container">
            <div className="row db-item"> 
              <div className="col-2 item-comp">
                <h5><strong>Product ID:</strong> {post.ProductID}</h5>
              </div>
              <div className="col-1 item-comp">
                <p><strong>SKU:</strong> {post.SKU}</p>
              </div>
              <div className="col-4 item-comp">
                <p value={post._id}><strong>ProductDescription:</strong> {post.ProductDescription}</p>
              </div>
              <div className="col-3 item-comp">
                <p><strong>_id:</strong> {post._id}</p>
              </div>
              <div className="col-2 item-comp">
                <p className="delete-item" onClick={this.delete.bind(this, post)} value={post._id}>Delete this Bin</p>
              </div>
            </div>
          </div>
        </li>
      </ul>
    ));
  };

  render() {

    console.log('State: ', this.state);

    return(


<div>

<Banner></Banner>
<div className="main-container">
        <div className="banner"><h1>Products</h1></div>
          <br></br>
        <div className="bin-posts">
          {this.displayProductPosts(this.state.posts)}
        </div>

        <div className="add">
          <div className="banner"><h4>Add Products</h4></div>
            <div className="add-body">
            <hr></hr>
            <form onSubmit={this.submit}>
            <div className="form-input">
                      <input
                        type="text"
                        name="ProductID"
                        value={this.state.ProductID}
                        placeholder="Product ID"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="SKU"
                        value={this.state.SKU}
                        placeholder="SKU"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="ProductDescription"
                        value={this.state.ProductDescription}
                        placeholder="Product Description"
                        onChange={this.handleChange}
                      /> 
                    </div>
              <button>Add</button>
            </form>
        </div>
        </div>
    </div>
    </div>
    );
  }
}

export default Products;
