import React from 'react';
import axios from 'axios';

import './App.css';

class Order extends React.Component {

  state = {
    OrderID: '',
    OrderNumber: '',
    DateOrdered: '',
    CustomerName: '',
    CustomerAddress: '',
    posts: []
  };

  componentDidMount = () => {
    this.getOrders();
  };

  getOrders = () => {
    axios.get('/api/orders')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved');
      })
      .catch(() => {
        // alert('error retrieving data');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({ [name]: value });
  };

  submit = (event) => {
    event.preventDefault();

    const payload = {
        OrderID: this.state.OrderID,
        OrderNumber: this.state.OrderNumber,
        DateOrdered: this.state.DateOrdered,
        CustomerName: this.state.CustomerName,
        CustomerAddress: this.state.CustomerAddress
    };

    axios({
      url: 'http://localhost:3000/api/saveOrder',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInputs();
        this.getInventory();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  resetUserInputs = () => {
    this.setState({
        OrderID: '',
        OrderNumber: '',
        DateOrdered: '',
        CustomerName: '',
        CustomerAddress: ''
    });
  };

  displayOrderPosts = (posts) => {

    if (!posts.length) return null;

    return posts.map((post, index) => (
      <div className="individual" key={index}>
        <h3>Order ID: {post.OrderID}</h3>
        <p>Order Number: {post.OrderNumber}</p>
        <p>Date Ordered: {post.DateOrdered}</p>
        <p>Customer Name: {post.CustomerName}</p>
        <p>Customer Address: {post.CustomerAddress}</p>
      </div>
    ));
  };

  render() {

    console.log('State: ', this.state);

    return(
      <div className="main-container-inventory">
        <h1>Orders</h1>
        <hr></hr>
          <div className="content-container">
              <div className="add">
                  <h4>Add to orders</h4>
                  <hr></hr>
                  <form onSubmit={this.submit}>
                    <div className="form-input">
                      <input
                        type="text"
                        name="OrderID"
                        value={this.state.OrderID}
                        placeholder="Order ID"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="OrderNumber"
                        value={this.state.OrderNumber}
                        placeholder="Order Number"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="DateOrdered"
                        value={this.state.DateOrdered}
                        placeholder="Date Ordered"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="CustomerName"
                        value={this.state.CustomerName}
                        placeholder="Customer Name"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="CustomerAddress"
                        value={this.state.CustomerAddress}
                        placeholder="Customer Address"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <button>Submit</button>
                  </form>
              </div>
          </div>
          <br></br>
        <div className="bin-posts">
          {this.displayOrderPosts(this.state.posts)}
        </div>

      </div>
    );
  }
}

export default Order;
