import React from 'react';
import axios from 'axios';

import './App.css';

class Bins extends React.Component {

  state = {
    BinID: '',
    BinName: '',
    posts: []
  };

  componentDidMount = () => {
    this.getBins();
  };

  getBins = () => {
    axios.get('/api/bins')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved');
      })
      .catch(() => {
        // alert('error retrieving data');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({ [name]: value });
  };

  submit = (event) => {
    event.preventDefault();

    const payload = {
      BinID: this.state.BinID,
      BinName: this.state.BinName
    };

    axios({
      url: 'http://localhost:3000/api/saveBin',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInputs();
        this.getBins();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  resetUserInputs = () => {
    this.setState({
      BinID: '',
      BinName: ''
    });
  };

  deleteItem = (event) => {
    console.log(event);
  };

  displayBinPosts = (posts) => {

    if (!posts.length) return null;

    return posts.map((post, index) => (
      <div className="individual" key={index}>
        <h3>Bin ID: {post.BinID}</h3>
        <p>Bin Name: {post.BinName}</p>
        <p className="delete-item" onClick="deleteItem">Delete this Bin</p>
      </div>
    ));
  };

  render() {

    console.log('State: ', this.state);

    return(
      <div className="main-container-bin">
        <h1>Bins</h1>
        <hr></hr>
          <div className="content-container">
              <div className="add">
                  <h4>Add a bin</h4>
                  <hr></hr>
                  <form onSubmit={this.submit}>
                    <div className="form-input">
                      <input
                        type="text"
                        name="BinID"
                        value={this.state.BinID}
                        placeholder="Bin ID"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="BinName"
                        value={this.state.BinName}
                        placeholder="Bin Name"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <button>Submit</button>
                  </form>
              </div>
          </div>
          <br></br>
        <div className="bin-posts">
          {this.displayBinPosts(this.state.posts)}
        </div>

      </div>
    );
  }
}

export default Bins;
