import React from 'react';
import axios from 'axios';
// import Banner from './banner';

import './App.css';

class Bins extends React.Component {

  state = {
    BinID: '',
    BinName: '',
    _id: '',
    posts: []
  };

  componentDidMount = () => {
    this.getBins();
  };

  getBins = () => {
    axios.get('/api/bins')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved');
      })
      .catch(() => {
        // alert('error retrieving data');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({ [name]: value });
  };

  submit = (event) => {
    event.preventDefault();

    const payload = {
      BinID: this.state.BinID,
      BinName: this.state.BinName
      // _id: this.state._id
    };

    axios({
      url: 'http://localhost:3000/api/saveBin',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInputs();
        this.getBins();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  delete = (data, event) => {
    event.preventDefault();

    console.log('Data before sending to server: ', data._id);

    axios({
      url: 'http://localhost:3000/api/deleteBin/' + data._id ,
      method: 'POST',
      data: data._id
    })
      .then(() => {
        console.log('Data has been sent to the server');
        // this.resetUserInputs();
        // this.getBins();
      })
      .catch(() => {
        console.log('Internal server error');
      });

      this.getBins();
  };

  update = (data, event) => {
    event.preventDefault();

    const payload = {
      BinID: this.state.BinID,
      BinName: this.state.BinName,
      _id: this.state._id
    };

    console.log('Data before sending to server: ', data._id);
    console.log('Data before sending to server: ', data.BinName);
    console.log('Data before sending to server: ', data.BinID);

    axios({
      url: 'http://localhost:3000/api/updateBin/' + data._id ,
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
      })
      .catch(() => {
        console.log('Internal server error');
      });

      // this.getBins();
  };

  addModal = () => {

  }

  resetUserInputs = () => {
    this.setState({
      BinID: '',
      BinName: '',
      _id: ''
    });
  };

  displayBinPosts = (posts) => {

    if (!posts.length) return null;

    return posts.map((post, index) => (

      <ul>
        <li>
          <div className="container">
            <div className="row db-item" key={index}> 
              <div className="col-3 item-comp">
                <h5><strong>Bin ID:</strong> {post.BinID}</h5>
              </div>
              <div className="col-3 item-comp">
                <p><strong>Bin Name:</strong> {post.BinName}</p>
              </div>
              <div className="col-4 item-comp">
                <p value={post._id}><strong>_id:</strong> {post._id}</p>
              </div>
              <div className="col-2 item-comp">
                <p className="delete-item" onClick={this.delete.bind(this, post)} value={post._id}>Delete this Bin</p>
              </div>
            </div>
          </div>
        </li>
      </ul>

    ));
  };

  render() {

    console.log('State: ', this.state);

    return(
      <div>
      <div className="dummy-links">
        <i className="fa fa-download icons"></i>
        <i className="fa fa-bell icons"></i>
        <i className="fa fa-print icons"></i>
        <i className="fa fa-bookmark icons"></i>
        <i className="fa fa-database icons"></i>
        <i className="fa fa-pause icons"></i>
        <i className="fa fa-plus icons" onClick={this.addModal}></i>
      </div>  

      <div className="main-container">
        <div className="banner"><h1>Bins</h1></div>
          <br></br>
        <div className="bin-posts">
          {this.displayBinPosts(this.state.posts)}
        </div>

        <div id="addd" className="add">
          <div className="banner"><h4>Add a bin</h4></div>
            <div className="add-body">
            <hr></hr>
            <form onSubmit={this.submit}>
              <div className="form-input">
                <input
                  type="text"
                  name="BinID"
                  value={this.state.BinID}
                  placeholder="Bin ID"
                  onChange={this.handleChange}
                /> 
              </div>
              <div className="form-input">
                <input
                  type="text"
                  name="BinName"
                  value={this.state.BinName}
                  placeholder="Bin Name"
                  onChange={this.handleChange}
                /> 
              </div>
              <button>Add</button>
            </form>
        </div>
        </div>


        <div className="update">
          <div className="banner"><h4>Update bin</h4></div>
            <div className="add-body">
            <hr></hr>
            <form onSubmit={this.update}>
            <div className="form-input">
                <input
                  type="text"
                  name="_id"
                  value={this.state._id}
                  placeholder="Unique Document ID"
                  onChange={this.handleChange}
                /> 
              </div>
              <div className="form-input">
                <input
                  type="text"
                  name="BinID"
                  value={this.state.BinID}
                  placeholder="Bin ID"
                  onChange={this.handleChange}
                /> 
              </div>
              <div className="form-input">
                <input
                  type="text"
                  name="BinName"
                  value={this.state.BinName}
                  placeholder="Bin Name"
                  onChange={this.handleChange}
                /> 
              </div>
              <button>Update</button>
            </form>
        </div>
        </div>

    </div>
    </div>
    );
  }
}

export default Bins;
