import React from 'react';
import axios from 'axios';
// import Banner from './banner';

import './App.css';

class Inventory extends React.Component {

  state = {
    InventoryID: '',
    ProductID: '',
    BinID: '',
    QTY: '',
    _id: '',
    posts: []
  };

  componentDidMount = () => {
    this.getInventory();
  };

  getInventory = () => {
    axios.get('/api/inventory')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved');
      })
      .catch(() => {
        // alert('error retrieving data');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({ [name]: value });
  };

  submit = (event) => {
    event.preventDefault();

    const payload = {
        InventoryID: this.state.InventoryID,
        ProductID: this.state.ProductID,
        BinID: this.state.BinID,
        QTY: this.state.QTY,
        // _id: this.state._id
    };

    axios({
      url: 'http://localhost:3000/api/saveInventory',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInputs();
        this.getInventory();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  delete = (data, event) => {
    event.preventDefault();

    console.log('Data before sending to server: ', data._id);

    axios({
      url: 'http://localhost:3000/api/deleteInventory',
      method: 'POST',
      data: data._id
    })
      .then(() => {
        console.log('Data has been sent to the server');
        // this.resetUserInputs();
        // this.getInventory();
      })
      .catch(() => {
        console.log('Internal server error');
      });
      this.getInventory();
  };

  resetUserInputs = () => {
    this.setState({
        InventoryID: '',
        ProductID: '',
        BinID: '',
        QTY: '',
        _id: ''
    });
  };

  displayInventoryPosts = (posts) => {

    if (!posts.length) return null;

    return posts.map((post, index) => (

      <ul>
        <li>
          <div className="container">
            <div className="row db-item"> 
              <div className="col-2 item-comp">
                <h5><strong>Inventory ID:</strong> {post.InventoryID}</h5>
              </div>
              <div className="col-2 item-comp">
                <p><strong>Product ID:</strong> {post.ProductID}</p>
              </div>
              <div className="col-1 item-comp">
                <p><strong>Bin ID:</strong> {post.BinID}</p>
              </div>
              <div className="col-2 item-comp">
                <p><strong>QTY:</strong> {post.QTY}</p>
              </div>
              <div className="col-3 item-comp">
                <p value={post._id}><strong>_id:</strong> {post._id}</p>
              </div>
              <div className="col-2 item-comp">
                <p className="delete-item" onClick={this.delete.bind(this, post)} value={post._id}>Delete this Bin</p>
              </div>
            </div>
          </div>
        </li>
      </ul>

    ));
  };

  render() {

    console.log('State: ', this.state);

    return(

      <div>
      <div className="dummy-links">
        <i className="fa fa-download icons"></i>
        <i className="fa fa-bell icons"></i>
        <i className="fa fa-print icons"></i>
        <i className="fa fa-bookmark icons"></i>
        <i className="fa fa-database icons"></i>
        <i className="fa fa-pause icons"></i>
        <i className="fa fa-plus icons"></i>
      </div>  
      <div className="main-container">
        <div className="banner"><h1>Inventory</h1></div>
          <br></br>
        <div className="bin-posts">
          {this.displayInventoryPosts(this.state.posts)}
        </div>

        <div className="add">
          <div className="banner"><h4>Add to Inventory</h4></div>
            <div className="add-body">
            <hr></hr>
            <form onSubmit={this.submit}>
            <div className="form-input">
                       <input
                         type="text"
                         name="InventoryID"
                         value={this.state.InventoryID}
                         placeholder="Inventory ID"
                         onChange={this.handleChange}
                       /> 
                     </div>
                     <div className="form-input">
                       <input
                         type="text"
                         name="ProductID"
                         value={this.state.ProductID}
                         placeholder="Product ID"
                         onChange={this.handleChange}
                       /> 
                     </div>
                     <div className="form-input">
                       <input
                         type="text"
                         name="BinID"
                         value={this.state.BinID}
                         placeholder="Bin ID"
                         onChange={this.handleChange}
                       /> 
                     </div>
                     <div className="form-input">
                       <input
                         type="text"
                         name="QTY"
                         value={this.state.QTY}
                         placeholder="QTY"
                         onChange={this.handleChange}
                       /> 
                     </div>
              <button>Add</button>
            </form>
        </div>
        </div>
    </div>
    </div>

    );
  }
}

export default Inventory;
