import React from 'react';
import axios from 'axios';
import Banner from './banner';

import './App.css';

class Bins extends React.Component {

  state = {
    BinID: '',
    BinName: '',
    _id: '',
    posts: []
  };

  componentDidMount = () => {
    this.getBins();
  };

  getBins = () => {
    axios.get('/api/bins')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved');
      })
      .catch(() => {
        // alert('error retrieving data');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({ [name]: value });
  };

  submit = (event) => {
    event.preventDefault();

    const payload = {
      BinID: this.state.BinID,
      BinName: this.state.BinName
      // _id: this.state._id
    };

    axios({
      url: 'http://localhost:3000/api/saveBin',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInputs();
        this.getBins();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  delete = (data, event) => {
    event.preventDefault();

    console.log('Data before sending to server: ', data._id);



    axios({
      url: 'http://localhost:3000/api/deleteBin/' + data._id ,
      method: 'POST',
      data: data._id
    })
      .then(() => {
        console.log('Data has been sent to the server');
        // this.resetUserInputs();
        // this.getBins();
      })
      .catch(() => {
        console.log('Internal server error');
      });

      this.getBins();
  };

  resetUserInputs = () => {
    this.setState({
      BinID: '',
      BinName: '',
      _id: ''
    });
  };

  displayBinPosts = (posts) => {

    if (!posts.length) return null;

    return posts.map((post, index) => (

      <ul>
        <li>
          <div className="container">
            <div className="row db-item"> 
              <div className="col-3 item-comp">
                <h5><strong>Bin ID:</strong> {post.BinID}</h5>
              </div>
              <div className="col-3 item-comp">
                <p><strong>Bin Name:</strong> {post.BinName}</p>
              </div>
              <div className="col-4 item-comp">
                <p value={post._id}><strong>_id:</strong> {post._id}</p>
              </div>
              <div className="col-2 item-comp">
                <p className="delete-item" onClick={this.delete.bind(this, post)} value={post._id}>Delete this Bin</p>
              </div>
            </div>
          </div>
        </li>
      </ul>

    ));
  };

  render() {

    console.log('State: ', this.state);

    return(
      <div>
        <Banner></Banner>
      <div className="main-container">
        <div className="banner"><h1>Bins</h1></div>
          <br></br>
        <div className="bin-posts">
          {this.displayBinPosts(this.state.posts)}
        </div>

        <div className="add">
          <div className="banner"><h4>Add a bin</h4></div>
            <div className="add-body">
            <hr></hr>
            <form onSubmit={this.submit}>
              <div className="form-input">
                <input
                  type="text"
                  name="BinID"
                  value={this.state.BinID}
                  placeholder="Bin ID"
                  onChange={this.handleChange}
                /> 
              </div>
              <div className="form-input">
                <input
                  type="text"
                  name="BinName"
                  value={this.state.BinName}
                  placeholder="Bin Name"
                  onChange={this.handleChange}
                /> 
              </div>
              <button>Add</button>
            </form>
        </div>
        </div>


        <div className="add">
          <div className="banner"><h4>Add a bin</h4></div>
            <div className="add-body">
            <hr></hr>
            <form onSubmit={this.submit}>
              <div className="form-input">
                <input
                  type="text"
                  name="BinID"
                  value={this.state.BinID}
                  placeholder="Bin ID"
                  onChange={this.handleChange}
                /> 
              </div>
              <div className="form-input">
                <input
                  type="text"
                  name="BinName"
                  value={this.state.BinName}
                  placeholder="Bin Name"
                  onChange={this.handleChange}
                /> 
              </div>
              <button>Add</button>
            </form>
        </div>
        </div>

    </div>
    </div>
    );
  }
}

export default Bins;
