import React from 'react';
import axios from 'axios';
import Banner from './banner';

import './App.css';

class Order extends React.Component {

  state = {
    OrderID: '',
    OrderNumber: '',
    DateOrdered: '',
    CustomerName: '',
    CustomerAddress: '',
    _id: '',
    posts: []
  };

  componentDidMount = () => {
    this.getOrders();
  };

  getOrders = () => {
    axios.get('/api/orders')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved');
      })
      .catch(() => {
        // alert('error retrieving data');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({ [name]: value });
  };

  submit = (event) => {
    event.preventDefault();

    const payload = {
        OrderID: this.state.OrderID,
        OrderNumber: this.state.OrderNumber,
        DateOrdered: this.state.DateOrdered,
        CustomerName: this.state.CustomerName,
        CustomerAddress: this.state.CustomerAddress
        // _id: this.state._id
    };

    axios({
      url: 'http://localhost:3000/api/saveOrder',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInputs();
        this.getOrders();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  delete = (data, event) => {
    event.preventDefault();

    console.log('Data before sending to server: ', data._id);

    axios({
      url: 'http://localhost:3000/api/deleteOrder',
      method: 'POST',
      data: data._id
    })
      .then(() => {
        console.log('Data has been sent to the server');
        // this.resetUserInputs();
        // this.getOrders();
      })
      .catch(() => {
        console.log('Internal server error');
      });
      this.getOrders();
  };

  update = (data, event) => {
    event.preventDefault();

    const payload = {
      OrderID: this.state.OrderID,
      OrderNumber: this.state.OrderNumber,
      DateOrdered: this.state.DateOrdered,
      CustomerName: this.state.CustomerName,
      CustomerAddress: this.state.CustomerAddress,
      _id: this.state._id
    };

    axios({
      url: 'http://localhost:3000/api/updateOrder/' + data._id + '/' + data.OrderID + '/' + data.OrderNumber + '/' + data.DateOrdered + '/' + data.CustomerName + '/' + data.CustomerAddress,
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
      })
      .catch(() => {
        console.log('Internal server error');
      });
      this.resetUserInputs();
      this.getOrders();
  };

  resetUserInputs = () => {
    this.setState({
        OrderID: '',
        OrderNumber: '',
        DateOrdered: '',
        CustomerName: '',
        CustomerAddress: '',
        _id: ''
    });
  };

  displayOrderPosts = (posts) => {

    if (!posts.length) return null;

    return posts.map((post, index) => (

      <ul>
        <li>
          <div className="container">
            <div className="row db-item"> 
              <div className="col-2 item-comp">
                <h5><strong>Order ID:</strong> {post.OrderID}</h5>
              </div>
              <div className="col-1 item-comp">
                <p><strong>Order Number:</strong> {post.OrderNumber}</p>
              </div>
              <div className="col-1 item-comp">
                <p><strong>Date Ordered:</strong> {post.DateOrdered}</p>
              </div>
              <div className="col-2 item-comp">
                <p><strong>Customer Name:</strong> {post.CustomerName}</p>
              </div>
              <div className="col-2 item-comp">
                <p><strong>Customer Address:</strong> {post.CustomerAddress}</p>
              </div>
              <div className="col-3 item-comp">
                <p><strong>_id:</strong> {post._id}</p>              
              </div>

              <div className="col-1 item-comp">
                <p className="delete-item" onClick={this.delete.bind(this, post)} value={post._id}>Delete</p>
              </div>
            </div>
          </div>
        </li>
      </ul>

    ));
  };

  render() {

    console.log('State: ', this.state);

    return(

      <div>
        <Banner></Banner>
      <div className="main-container">
        <div className="banner"><h1>Orders</h1></div>
          <br></br>
        <div className="bin-posts">
          {this.displayOrderPosts(this.state.posts)}
        </div>

        <div className="add">
          <div className="banner"><h4>Add an Order</h4></div>
            <div className="add-body">
            <hr></hr>
            <form onSubmit={this.submit}>
            <div className="form-input">
                      <input
                        type="text"
                        name="OrderID"
                        value={this.state.OrderID}
                        placeholder="Order ID"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="OrderNumber"
                        value={this.state.OrderNumber}
                        placeholder="Order Number"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="DateOrdered"
                        value={this.state.DateOrdered}
                        placeholder="Date Ordered"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="CustomerName"
                        value={this.state.CustomerName}
                        placeholder="Customer Name"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="CustomerAddress"
                        value={this.state.CustomerAddress}
                        placeholder="Customer Address"
                        onChange={this.handleChange}
                      /> 
                    </div>
              <button>Add</button>
            </form>
        </div>
        </div>
    </div>
    </div>



