import React from 'react';
import axios from 'axios';
import Banner from './banner';

import './App.css';

class Order extends React.Component {

  state = {
    OrderID: '',
    OrderNumber: '',
    DateOrdered: '',
    CustomerName: '',
    CustomerAddress: '',
    _id: '',
    posts: []
  };

  componentDidMount = () => {
    this.getOrders();
  };

  getOrders = () => {
    axios.get('/api/orders')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved');
      })
      .catch(() => {
        // alert('error retrieving data');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({ [name]: value });
  };

  submit = (event) => {
    event.preventDefault();

    const payload = {
        OrderID: this.state.OrderID,
        OrderNumber: this.state.OrderNumber,
        DateOrdered: this.state.DateOrdered,
        CustomerName: this.state.CustomerName,
        CustomerAddress: this.state.CustomerAddress
        // _id: this.state._id
    };

    axios({
      url: 'http://localhost:3000/api/saveOrder',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInputs();
        this.getOrders();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  delete = (data, event) => {
    event.preventDefault();

    console.log('Data before sending to server: ', data._id);

    axios({
      url: 'http://localhost:3000/api/deleteOrder',
      method: 'POST',
      data: data._id
    })
      .then(() => {
        console.log('Data has been sent to the server');
        // this.resetUserInputs();
        this.getOrders();
      })
      .catch(() => {
        console.log('Internal server error');
      });
      // this.getBins();
  };

  resetUserInputs = () => {
    this.setState({
        OrderID: '',
        OrderNumber: '',
        DateOrdered: '',
        CustomerName: '',
        CustomerAddress: '',
        _id: ''
    });
  };

  displayOrderPosts = (posts) => {

    if (!posts.length) return null;

    return posts.map((post, index) => (

      <ul>
        <li>
          <div className="container">
            <div className="row db-item"> 
              <div className="col-3 item-comp">
                <h5><strong>Bin ID:</strong> {post.BinID}</h5>
              </div>
              <div className="col-3 item-comp">
                <p><strong>Bin Name:</strong> {post.BinName}</p>
              </div>
              <div className="col-4 item-comp">
                <p value={post._id}><strong>_id:</strong> {post._id}</p>
              </div>
              <div className="col-2 item-comp">
                <p className="delete-item" onClick={this.delete.bind(this, post)} value={post._id}>Delete this Bin</p>
              </div>
            </div>
          </div>
        </li>
      </ul>



      /* <div className="individual" key={index}>
        <h3>Order ID: {post.OrderID}</h3>
        <p>Order Number: {post.OrderNumber}</p>
        <p>Date Ordered: {post.DateOrdered}</p>
        <p>Customer Name: {post.CustomerName}</p>
        <p>Customer Address: {post.CustomerAddress}</p>
        <p>_id: {post._id}</p>
      </div> */
    ));
  };

  render() {

    console.log('State: ', this.state);

    return(
      <div>
      <NavBar></NavBar>
      <div className="main-container">
        <h1>Orders</h1>
        <hr></hr>
          <div className="content-container">
              <div className="add">
                  <h4>Add to orders</h4>
                  <hr></hr>
                  <form onSubmit={this.submit}>
                    <div className="form-input">
                      <input
                        type="text"
                        name="OrderID"
                        value={this.state.OrderID}
                        placeholder="Order ID"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="OrderNumber"
                        value={this.state.OrderNumber}
                        placeholder="Order Number"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="DateOrdered"
                        value={this.state.DateOrdered}
                        placeholder="Date Ordered"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="CustomerName"
                        value={this.state.CustomerName}
                        placeholder="Customer Name"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="CustomerAddress"
                        value={this.state.CustomerAddress}
                        placeholder="Customer Address"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <button>Submit</button>
                  </form>
              </div>
          </div>
          <br></br>
        <div className="bin-posts">
          {this.displayOrderPosts(this.state.posts)}
        </div>
      </div>
      </div>
    );
  }
}

export default Order;
