// Copyright Cole Snyder 2020 
import React from 'react';
import axios from 'axios';
import { Container, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';


import './App.css';

class Order extends React.Component {

  state = {
    OrderID: '',
    OrderNumber: '',
    DateOrdered: '',
    CustomerName: '',
    CustomerAddress: '',
    _id: '',
    posts: [],
    modalIsOpen: false,
    updateModalIsOpen: false
  };

  componentDidMount = () => {
    this.getOrders();
  };

  getOrders = () => {
    axios.get('/api/orders')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved');
      })
      .catch(() => {
        // alert('error retrieving data');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;

    this.setState({ [name]: value });
  };

  submit = (event) => {
    event.preventDefault();

    const payload = {
        OrderID: this.state.OrderID,
        OrderNumber: this.state.OrderNumber,
        DateOrdered: this.state.DateOrdered,
        CustomerName: this.state.CustomerName,
        CustomerAddress: this.state.CustomerAddress
    };

    axios({
      url: 'http://localhost:3000/api/saveOrder',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInputs();
        this.getOrders();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  delete = (data, event) => {
    event.preventDefault();

    console.log('Data before sending to server: ', data._id);

    axios({
      url: 'http://localhost:3000/api/deleteOrder/' + data._id,
      method: 'POST',
      data: data._id
    })
      .then(() => {
        console.log('Data has been sent to the server');
      })
      .catch(() => {
        console.log('Internal server error');
      });
      this.getOrders();
  };

  update = (data, event) => {
    event.preventDefault();

    const payload = {
      OrderID: this.state.OrderID,
      OrderNumber: this.state.OrderNumber,
      DateOrdered: this.state.DateOrdered,
      CustomerName: this.state.CustomerName,
      CustomerAddress: this.state.CustomerAddress,
      _id: this.state._id
    };

    axios({
      url: 'http://localhost:3000/api/updateOrder/' + data._id + '/' + data.OrderID + '/' + data.OrderNumber + '/' + data.DateOrdered + '/' + data.CustomerName + '/' + data.CustomerAddress,
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
      })
      .catch(() => {
        console.log('Internal server error');
      });
      this.resetUserInputs();
      this.getOrders();
      this.setState({
        updateModalIsOpen: ! this.state.updateModalIsOpen
      });
  };

  resetUserInputs = () => {
    this.setState({
        OrderID: '',
        OrderNumber: '',
        DateOrdered: '',
        CustomerName: '',
        CustomerAddress: '',
        _id: ''
    });
  };

  displayOrderPosts = (posts) => {

    if (!posts.length) return null;

    return posts.map((post, index) => (

      <ul>
        <li>
          <div className="container">
            <div className="row db-item"> 
              <div className="col-2 item-comp">
                <h5><strong>Order ID:</strong> {post.OrderID}</h5>
              </div>
              <div className="col-1 item-comp">
                <p><strong>Order Number:</strong> {post.OrderNumber}</p>
              </div>
              <div className="col-1 item-comp">
                <p><strong>Date Ordered:</strong> {post.DateOrdered}</p>
              </div>
              <div className="col-2 item-comp">
                <p><strong>Customer Name:</strong> {post.CustomerName}</p>
              </div>
              <div className="col-2 item-comp">
                <p><strong>Customer Address:</strong> {post.CustomerAddress}</p>
              </div>
              <div className="col-3 item-comp">
                <p><strong>_id:</strong><a className="idClick" onClick={this.setModal.bind(this, post)}>{post._id}</a></p>              
              </div>

              <div className="col-1 item-comp">
                <p className="delete-item" onClick={this.delete.bind(this, post)} value={post._id}>Delete</p>
              </div>
            </div>
          </div>
        </li>
      </ul>

    ));
  };

  toggleModal = () => {
    this.setState({
      modalIsOpen: ! this.state.modalIsOpen
    });
}

  // function toggles modal
  toggleModal = () => {
    this.setState({
      modalIsOpen: ! this.state.modalIsOpen
    });
}

  // function toggles modal
  toggleUpdateModal = () => {
    this.setState({
      updateModalIsOpen: ! this.state.updateModalIsOpen
    });
}

  // function sets state of update modal
  setModal = (data, event) => {
    this.setState({
      BinID: data.BinID,
      BinName: data.BinName,
      _id: data._id
    });

    this.toggleUpdateModal();

}

  render() {

    console.log('State: ', this.state);

    return(

      <div>

<Container>
  <Modal isOpen={this.state.modalIsOpen}>
    <ModalHeader toggle={this.toggleModal.bind(this)}>
      Orders
    </ModalHeader>
    <ModalBody>
    <div className="add">
          <div className="banner"><h4>Add an Order</h4></div>
            <div className="add-body">
            <form onSubmit={this.submit}>
            <div className="form-input">
                      <input
                        type="text"
                        name="OrderID"
                        value={this.state.OrderID}
                        placeholder="Order ID"
                        onChange={this.handleChange}
                      /> 
                    </div>
                    <div className="form-input">
                      <input
                        type="text"
                        name="OrderNumber"
                        value={this.state.OrderNumber}
                        placeholder="Order Number"
